import React, { useState } from 'react';
import './GuestPage.css';

const GuestPage = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleJoin = () => {
    console.log('Join button clicked');
    // Implement the logic to join the chat room here
  };

  const handleLeave = () => {
    console.log('Leave button clicked');
    // Implement the logic to leave the chat room here
  };

  return (
    <div className="GuestChatContainer">
      <div className="roomColumn">
        Current Rooms
        <div className="chatCSS">
          <button className="chatRoomButton" onClick={toggleMenu}>
            General Chat
            {isMenuOpen && (
              <div className="popup-menu">
                <button onClick={handleJoin}>Join</button>
                <button onClick={handleLeave}>Leave</button>
              </div>
            )}
          </button>
        </div>
      </div>
      <div className="chatColumn">Current Chat</div>
      <div className="userColumn">Current Online</div>
    </div>
  );
};

export default GuestPage;
